mapper.update:
update coinlog set
id = id

	, groupId=#{groupId}


	, stuUserId=#{stuUserId}


	, coin=#{coin}


	, money=#{money}


	, createTime=#{createTime}

where
id=#{id}

mapper.select:
select
id,
groupId,
stuUserId,
money,
coin,
left(FROM_UNIXTIME(left(createTime,10)),19) as createTime
from coinlog

	
		id=#{id}
	
	
		and groupId=#{groupId}
	
	
		and stuUserId=#{stuUserId}
	
	
		and money=#{money}
	
	
		and coin=#{coin}
	
	
		and createTime=#{createTime}

select
id,
groupId,
stuUserId,
money,
coin,
left(FROM_UNIXTIME(left(createTime,10)),19) as createTime
from coinlog

	
		id=#{id}
	
	
		and groupId=#{groupId}
	
	
		and stuUserId=#{stuUserId}
	
	
		and money=#{money}
	
	
		and coin=#{coin}
	
	
		and createTime=#{createTime}
